import React, { useState } from 'react';

export const Form = () => {
  const [step, setStep] = useState(1);

  const handleNextStep = () => {
    setStep(step + 1);
  };

  const handlePreviousStep = () => {
    setStep(step - 1);
  };

  return (
    <div className="max-w-4xl mx-auto p-8 bg-white rounded-lg shadow-lg">
      {step === 1 && (
        <div>
          <h2 className="text-2xl font-semibold mb-6">Step 1: Select Your Major(s)</h2>
          <div className="mb-4">
            <label className="block  font-medium mb-2">Major</label>
            <input
              type="text"
              className="block w-full border border-gray-300 rounded-lg p-3 focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 font-medium mb-2">College</label>
            <select
              className="block w-full border border-gray-300 rounded-lg p-3 focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="">Select College</option>
              <option value="Engineering">College of Engineering</option>
              <option value="Arts">College of Arts</option>
            </select>
          </div>
          <div className="flex justify-between">
            <button
              type="button"
              className="bg-blue-500  px-6 py-2 rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
              onClick={handleNextStep}
            >
              Next
            </button>
          </div>
        </div>
      )}

      {/* Other steps go here */}
    </div>
  );
};

export default Form;
