import "/Users/ananyaanand/Desktop/bruinbot/src/App.css"
import{useState, useEffect} from "react"
import {Container, Row, Col} from "react-bootstrap"
import {ArrowRightCircle} from "react-bootstrap-icons"
import headerImg from "../assets/headerImg.png"

export const Banner = () => {
    const[loopNum, setLoopNum] = useState(0);
    const[isDeleting, setIsDeleting] = useState(false);
    const toRotate=["8 AMs", "Friday Classes", "stress"];
    const[text, setText] = useState("");
    const period = 2000;
    const[delta, setDelta] = useState(300 - Math.random() * 100) // determine how fast letter comes after 

    useEffect(() => {
        let ticker = setInterval(() => {
          tick();
        }, delta);

        return () => { clearInterval(ticker) };
      }, [text])

      const tick = () => {
        let i = loopNum % toRotate.length;
        let fullText = toRotate[i];
        let updatedText = isDeleting ? fullText.substring(0, text.length - 1) : fullText.substring(0, text.length + 1);

        setText(updatedText);

        if (isDeleting) {
          setDelta(prevDelta => prevDelta / 2);
        }

        if (!isDeleting && updatedText === fullText) {
          setIsDeleting(true);
          setDelta(period);
        } else if (isDeleting && updatedText === '') {
          setIsDeleting(false);
          setLoopNum(loopNum + 1);
          setDelta(500);
        } 
      }


    return(
        <section className="banner" id="home">
            <Container className="align-items-center">
                <Row>
                    <Col xs={12} md={6} xl={7}>
                    <span className="tagline">BruinBot</span>
                    <h1>{`Say No to `}<span className="wrap">{text}</span></h1>
                    <button onClick={() => {console.log('starting')}}>Get Started!<ArrowRightCircle size={25}/></button>
                    </Col>
                    <Col xs={12} md={6} xl={5}>
                    <img src={headerImg} alt="Bear" style={{ 
        height: '300px', /* Adjust size as needed */
        width: '200px',  /* Adjust size as needed */
        marginLeft: 'auto', /* Move the image to the right */
        marginRight: '20px', /* Adjust spacing from the right */
        display: 'block' /* Ensure the image is a block-level element for margin adjustments */
    }} ></img>
                    </Col>
                </Row>
            </Container>


        </section>
    )
}
