// src/components/MultiStepForm.js
import React, { useState } from 'react';
import "/Users/ananyaanand/Desktop/bruinbot/src/index.css"
import '/Users/ananyaanand/Desktop/bruinbot/src/index.js'; 

export const Form = () => {
  const [step, setStep] = useState(1);
  const [major, setMajor] = useState([{college: '', subject: '', technicalBreadth: ""}]);
  const [graduationDate, setGraduationDate] = useState({ season: '', year: '' });
  const [courses, setCourses] = useState([{ subject: '', title: '' }]);
  const [time, setTime] = useState({ start: '', end: '' });
  const [maxLoad, setMaxLoad] = useState('');
  const [summerClasses, setSummerClasses] = useState('');

  const handleAddCourse = () => {
    setCourses([...courses, { subject: '', title: '' }]);
  };

  const handleCourseChange = (index, field, value) => {
    const updatedCourses = [...courses];
    updatedCourses[index][field] = value;
    setCourses(updatedCourses);
  };

  const handleAddMajor = () => {
    setMajor([...major, {college: '', subject: '', technicalBreadth: ''}])
  }

  const handleMajorChange = (index, field, value) => {
    const updatedMajor = [...major];
    updatedMajor[index][field] = value;
    setMajor(updatedMajor)
  }


  const handleNextStep = () => {
    setStep(step + 1);
    }

  const handlePreviousStep = () => {
    setStep(step - 1);
  };

  const handleSubmit = () => {
   const submission = {
      major,
      graduationDate,
      courses,
      time,
      maxLoad,
      summerClasses,
    };
    console.log(submission);

    function getMajorPrompt(){
     let majors = "";
      for(let i = 0; i < submission.major.length - 1; i++)
      {
        majors += submission.major[i].subject + " from " + submission.major[i].college;

      }
      majors += "and " + submission.major[-1].subject + " from " + submission.major[-1].college;
      return majors;
    }



    let prompt = `Generate a quarter-by-quarter schedule starting from ${new Date().getFullYear}
    for a UCLA student wanting to major in ${getMajorPrompt} who has 
    already completed **math 31 A, math 32A, and cs 31**. The student wants to complete in 
    **3 years** and wants classes from **10 am** to **5 pm**. Include a short summary of what
     the course is about below each one. Account for UCLA's GE requirements and ensure each quarter 
     has at least **12 units** but no more than **16 units**. **Include summer quarters if necessary** 
     to complete the student's intended degree. Ensure prerequisites are met before taking a course. 
     Ensure all required courses for the majors the student has taken are completed in time. 
     The courses required for the major are below but ensure to not include the ones the student has 
     already completed in the course plan. Remember to include GEs.`


